INSERT INTO "CART_REQUEST_TOOL"."MATERIAL"(MATERIAL_ID, CATALOG_ID, DESCRIPTION, CODE, POPUP, CREATED_USER_ID) VALUES(136,90,'Associate Testing Consultant (E1)\\ Experience < 1 Years; No SAP Experience','49010850','Fresh graduate with computer science relevant background or working experience < 1 year. Have basic understanding of general testing process/methodology. Associate testing consultant is expected to execute the test case and generate test automation base on test case.',1);
INSERT INTO "CART_REQUEST_TOOL"."MATERIAL"(MATERIAL_ID, CATALOG_ID, DESCRIPTION, CODE, POPUP, CREATED_USER_ID) VALUES(137,91,'Testing Consultant (E2)\\ Experience > 3 Years; SAP Experience','49010851','Working experience > 3 years with ERP business knowledge or relevant. Familiar with both manual testing and test automation process/methodology. Testing consultant is expected to responsible for E2E testing activities of specifications including understand requirement, design the test case, execute the test case and test automation.',1);
INSERT INTO "CART_REQUEST_TOOL"."MATERIAL"(MATERIAL_ID, CATALOG_ID, DESCRIPTION, CODE, POPUP, CREATED_USER_ID) VALUES(138,92,'Senior Testing Consultant (E3)\\ Experience > 6 Years at Knowledge Architect Level','49010852','Working experience > 6 years with solid ERP business knowledge or relevant, strong project management skill and experience. Familiar with both manual testing and test automation process/methodology. Senior Testing consultant is expected to take E2E responsibility of one project''s testing and monitoring / guiding testing consultant and associate testing consultant.',1);
